import React from "react";
import { Container, Row, Col, Button } from "reactstrap";
import { Link } from "react-router-dom";
import "../styles/thank-you.css";

const About = () => {
  return (
    <section className="about">
      <Container>
        <Row>
          <Col lg="6" className="m-auto">
            <div className="about__content">
              <h2 className="about__title">About Our Travel Website</h2>
              <p>
                This travel website is a self-project developed by Aman Chhetri.
                We are passionate about providing you with the best travel
                experiences. Explore our featured tours and customize your
                journey to create lasting memories.
              </p>
              <Link to="/tours">
                <Button className="btn secondary__btn about__btn">
                  Explore Tours
                </Button>
              </Link>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default About;
